mapper.select:
select	ann_id, title, content, views, regdate, regip, adm_id
from   (select	ann_id, title, content, views, regdate, regip, adm_id,
				row_number() over(order by regdate desc) rnum
		from	announcement

	
				instr(${fieldStr}, #{keyword}) != 0
	

		)
where	rnum between #{startNum} and #{endNum}

select	title, content, views, regdate, regip, adm_id
from	announcement
where	ann_id = #{ann_id}

mapper.update:
update	announcement
set		views = views + 1
where	ann_id = #{ann_id}

update	announcement
set		content = #{content}, regip = #{regip}
where	ann_id = #{ann_id} and adm_id = #{adm_id}
