mapper.delete:
DELETE FROM TEST_QUESTION
WHERE TEST_Q_ID = #{qid}
  AND TEST_ID IN (
      SELECT t.TEST_ID
      FROM TEST t
      JOIN LECTURE l ON t.LECT_ID = l.LECT_ID
      WHERE l.INST_ID = #{instId}
        AND l.LECT_ID = #{lectId}
  )

mapper.select:
SELECT TEST_ID 
FROM TEST 
WHERE LECT_ID = #{lectId}
