mapper.select:
select	review_id, score, content, regdate, regip, user_id
from   (select	review_id, score, content, regdate, regip, user_id,
				row_number() over(order by regdate desc) rnum
		from	review

				lect_id = #{lectId}
	
				and instr(${fieldStr}, #{keyword}) != 0
	

		)
where	rnum between #{startNum} and #{endNum}

select	review_id, score, content, regdate, regip, lect_id, user_id
from	review
where	review_id = #{reviewId}

select	review_id, score, content, regdate, regip, lect_id, user_id
from	review
where	review_id = #{reviewId}

select	review_id, score, content, regdate, regip, lect_id, user_id
from	review
where	review_id = #{reviewId}

mapper.update:
update	review
set		score = #{score}, content = #{content}, regip = #{regip}
where	review_id = #{review_id} and user_id = #{user_id}
